import React, { useContext, useEffect, useState } from "react";
import MainDashboard from "../pages/MainDashboard";
import { EmployeeContext } from "../context/EmployeeContext";
import { Button } from "@mui/material";
import jsPDF from "jspdf";
import "jspdf-autotable";

const EmpPayslip = () => {
  const [employeesWithDetails, setEmployeesWithDetails] = useState([]);
  const { employees } = useContext(EmployeeContext);

  const companyDetails = {
    name: "Deepnap Softech",
    address: "5E 12/BP N.I.T Faridabad(121001)",
    logo: "data:image/png;base64,", // Update this path to your logo
  };

  const FUND_PERCENTAGE = 12; // Percentage of salary allocated to the fund

  useEffect(() => {
    if (employees && employees.length > 0) {
      const updatedEmployees = employees.map((employee) => {
        const presentDays = employee.attendance
          ? employee.attendance.filter((att) => att.status === "Present").length
          : 0;

        const fund = (employee.salary * FUND_PERCENTAGE) / 100;
        const totalSalary = employee.salary - fund;

        return { ...employee, workingDays: presentDays, fund, totalSalary };
      });

      setEmployeesWithDetails(updatedEmployees);
    }
  }, [employees]);

  const exportToPDF = (employee) => {
    const doc = new jsPDF();
    const pageWidth = doc.internal.pageSize.getWidth();

    // Add Company Logo (Optional)
    const companyLogo =
      "data:image/png;base64,"; // Replace with your logo's base64 or URL
    if (companyLogo) {
      doc.addImage(companyLogo, "PNG", 14, 10, 30, 30);
    }

    // Add Company Name and Address
    doc.setFont("helvetica", "bold");
    doc.setFontSize(16);
    doc.text(companyDetails.name, pageWidth / 2, 20, { align: "center" });
    doc.setFontSize(12);
    doc.setFont("helvetica", "normal");
    doc.text(companyDetails.address, pageWidth / 2, 28, { align: "center" });

    // Add Payslip Title
    doc.setFontSize(14);
    doc.setFont("helvetica", "bold");
    doc.text("Employee Salary Slip", pageWidth / 2, 50, { align: "center" });

    // Add Divider Line
    doc.setDrawColor(200, 200, 200);
    doc.line(14, 54, pageWidth - 14, 54);

    // Employee Details Section
    const startY = 60;
    const details = [
      `Employee Name: ${employee.firstName} ${employee.lastName}`,
      `Employee Code: ${employee.employeeCode}`,
      `Email: ${employee.email}`,
      `Department: ${employee.department}`,
      `Role: ${employee.role}`,
      `Salary Month: ${new Date().toLocaleString("default", {
        month: "long",
        year: "numeric",
      })}`,
    ];
    doc.setFontSize(10);
    details.forEach((text, index) => {
      doc.text(text, 14, startY + index * 8);
    });

    // Add another Divider Line
    doc.line(
      14,
      startY + details.length * 8 + 4,
      pageWidth - 14,
      startY + details.length * 8 + 4
    );

    // Salary Breakdown Section with AutoTable
    const breakdownStartY = startY + details.length * 8 + 12;
    doc.setFont("helvetica", "bold");
    doc.text("Salary Breakdown", 14, breakdownStartY);

    const salaryBreakdown = [
      ["Basic Salary", `${employee.salary.toFixed(2)}`],
      ["Fund (12%)", `${employee.fund.toFixed(2)}`],
      ["Deductions", "0.00"],
      ["Net Salary", `${(employee.salary - employee.fund).toFixed(2)}`],
    ];

    // Using AutoTable for consistent formatting
    doc.autoTable({
      startY: breakdownStartY + 8,
      theme: "grid",
      head: [["Description", "Amount"]],
      body: salaryBreakdown.map((row) => row.map(String)),
      columnStyles: {
        0: { cellWidth: 100 }, // Description column
        1: { cellWidth: 100 }, // Amount column
      },
      styles: { fontSize: 10 },
    });

    // Footer with Terms and Disclaimer
    const footerStartY = doc.lastAutoTable.finalY + 10;
    doc.setDrawColor(200, 200, 200);
    doc.line(14, footerStartY, pageWidth - 14, footerStartY);
    doc.setFontSize(10);
    doc.setFont("helvetica", "italic");
    doc.setTextColor(128, 128, 128);
    doc.text(
      "This salary slip is generated by the company and is intended for employee reference only.",
      14,
      footerStartY + 8
    );
    doc.text(
      "If you have any queries, please contact HR at hr@deepnapsoftech.com.",
      14,
      footerStartY + 16
    );
    doc.text(
      "This document was autogenerated, and the signature is not required.",
      14,
      footerStartY + 24
    );

    // Save the PDF
    doc.save(`${employee.firstName}_${employee.lastName}_Salary_Slip.pdf`);
  };

  return (
    <>
      <MainDashboard />
      <div className="relative min-h-screen overflow-x-auto pt-6 lg:ml-[15rem] xs:ml-[4rem] p-2 max-w-7xl bg-gray-100">
        <div className="bg-blue-600 text-white text-center py-4 mb-6 rounded-lg shadow-md">
          <h2 className="text-2xl font-bold">Generate Employee Payslip</h2>
        </div>
        <div className="bg-white shadow-md rounded-lg p-4 overflow-x-auto">
          <table className="w-full overflow-x-auto text-sm text-left rtl:text-right text-gray-500">
            <thead className="text-xs text-gray-700 uppercase bg-gray-200">
              <tr>
                <th className="px-6 py-3">Full Name</th>
                <th className="px-6 py-3">Department</th>
                <th className="px-6 py-3">Designation</th>
                <th className="px-6 py-3">Actual Salary</th>
                <th className="px-6 py-3">Working Days</th>
                <th className="px-6 py-3">Fund (12%)</th>
                <th className="px-6 py-3">Total Salary</th>
                <th className="px-6 py-3">Generate Payslip</th>
              </tr>
            </thead>
            <tbody>
              {employeesWithDetails.length > 0 ? (
                employeesWithDetails.map((user, index) => (
                  <tr
                    key={index}
                    className={`border-b transition-all ${
                      user.Empstatus === "terminated"
                        ? "bg-gray-100 text-gray-400"
                        : "bg-white"
                    }`}
                  >
                    <td className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap capitalize">
                      {user.firstName} {user.lastName}
                    </td>
                    <td className="px-6 py-4">{user.department}</td>
                    <td className="px-6 py-4">{user.role}</td>
                    <td className="px-6 py-4">₹{user.salary.toFixed(2)}</td>
                    <td className="px-6 py-4">{user.workingDays}</td>
                    <td className="px-6 py-4">₹{user.fund.toFixed(2)}</td>
                    <td className="px-6 py-4">
                      ₹{user.totalSalary.toFixed(2)}
                    </td>
                    <td className="px-6 py-4">
                      <Button
                        onClick={() => exportToPDF(user)}
                        variant="contained"
                        color="success"
                        size="small"
                      >
                        Payslip
                      </Button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td
                    colSpan="8"
                    className="px-6 py-4 text-center text-gray-500"
                  >
                    No data available
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};

export default EmpPayslip;
